import React from "react";
import { useState } from "react";
import "./form.css";
import { Link } from "react-router-dom";
import Regpic from "../../imgs/Regpic.jpg";
import axios from "axios";
import Address from "../Address/Address";

export default function Form() {
  const [formValues, setFormValues] = useState({
    userid: "",
    title: "",
    body: "",
  });
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    setFormErrors(validate(formValues));
    setIsSubmit(true);
    console.log(formValues);
    // axios.post("https://jsonplaceholder.typicode.com/posts", {
    //   username: formValues.username,
    //   email: formValues.email,
    //   password: formValues.password,
    //   phnumber: formValues.phnumber,
    //   picture: formValues.picture,
    // });
    // localStorage.setItem("current-user", JSON.stringify(formValues));

    alert(formValues.userid);
    alert(formValues.title);
    alert(formValues.body);
  };
  const validate = (values) => {
    const errors = {};
    if (!values.userid) {
      errors.userid = "Please select a user";
    }
    if (!values.title) {
      errors.title = "This field is required";
    }
    if (!values.body) {
      errors.body = "This field is required";
    }

    return errors;
  };

  return (
    <div>
      <div className="image">
        <img className="im" src={Regpic} alt="img" />
      </div>
      <div className="form">
        <center>
          <form onSubmit={handleSubmit}>
            <label>
              User :&nbsp;
              <input
                type="text"
                name="userid"
                placeholder="Your username..."
                val={formValues.userid}
                onChange={handleChange}
              />
            </label>
            <p className="error">{formErrors.userid}</p>
            <label>
              Title :&nbsp;
              <input
                type="text"
                placeholder="Enter title here..."
                name="title"
                val={formValues.title}
                onChange={handleChange}
              />
            </label>
            <p className="error">{formErrors.title}</p>
            <label>
              Body :&nbsp;
              <input
                type="password"
                placeholder="Enter body here..."
                name="body"
                val={formValues.body}
                onChange={handleChange}
              />
            </label>
            <p className="error">{formErrors.body}</p>
            <button className="Post">Post</button>
          </form>
        </center>
      </div>
      <div>
        <Address/>
      </div>
    </div>
  );
}
