import { useEffect, useState } from "react";
import "./form.css";
import { Link } from "react-router-dom";
import Regpic from "../../imgs/Regpic.jpg";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function Form() {
  const navigate = useNavigate();
  const [formValues, setFormValues] = useState({
    userid: "",
    title: "",
    body: "",
  });
  const [users, setUsers] = useState([]);
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);
  useEffect(() => {
    axios
      .get(`https://jsonplaceholder.typicode.com/users`)
      .then((res) => {
        setUsers(res.data);
        console.log(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setFormErrors(validate(formValues));
    setIsSubmit(true);
    // alert(formValues);

    // axios.post("http://localhost:5000/users", {
    //   username: formValues.username,
    //   email: formValues.email,
    //   password: formValues.password,
    //   phnumber: formValues.phnumber,
    //   picture: formValues.picture,
    // });
    // localStorage.setItem("current-user", JSON.stringify(formValues));

    alert(formValues.userid);
  };

  const validate = (values) => {
    const errors = {};
    if (!values.userid) {
      errors.userid = "Please select user";
    }
    if (!values.title) {
      errors.title = "This field is required!";
    }
    if (!values.body) {
      errors.body = "This field is required!";
    }
    return errors;
  };

  return (
    <div>
      <div className="image">
        <img className="img" src={Regpic} alt="img" />
      </div>
      <center>
        <div className="form">
          <center>
            <form className="registrationForm" onSubmit={handleSubmit}>
              <label className="label">
                Userid :&nbsp;
              </label>
              <select val={formValues.userid} onChange={handleChange}>
                  {users.map((user) => {
                    return (
                      <option className="input" val={user.id}val={user.id}>
                        {user.name}
                      </option>
                    );
                  })}
                </select>
              <p className="error">{formErrors.userid}</p>
              <label className="label">
                Title :&nbsp;
                <input
                  className="input"
                  type="text"
                  placeholder="Enter your email..."
                  name="title"
                  val={formValues.title}
                  onChange={handleChange}
                />
              </label>
              <p className="error">{formErrors.title}</p>
              <label className="label">
                BODY :&nbsp;
                <input
                  className="input"
                  type="text"
                  placeholder="Enter your password..."
                  name="body"
                  val={formValues.body}
                  onChange={handleChange}
                />
              </label>
              <p className="error">{formErrors.body}</p>
              <button className="Post">POST</button>
            </form>
          </center>
        </div>
      </center>
    </div>
  );
}
export default Form;
